import React, { useEffect, useState } from "react";

function BookList() {
  const [books, setBooks] = useState([]);
  const [page, setPage] = useState(1);
  const [inputPage, setInputPage] = useState("1"); // for input field (string)
  const [pageSize] = useState(5);
  const [sortBy, setSortBy] = useState("title");
  const [sortOrder, setSortOrder] = useState("asc");
  const [totalPages, setTotalPages] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchBooks = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(
        `http://localhost:5000/api/books?page=${page}&page_size=${pageSize}&sort_by=${sortBy}&sort_order=${sortOrder}`
      );
      if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
      const data = await res.json();
      if (!data.books || !Array.isArray(data.books)) {
        throw new Error("Invalid response: 'books' missing or not array");
      }
      if (typeof data.totalPages !== "number") {
        throw new Error("Invalid response: 'totalPages' missing or invalid");
      }
      setBooks(data.books);
      setTotalPages(data.totalPages);
    } catch (err) {
      setError(err.message);
      setBooks([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, [page, sortBy, sortOrder]);

  // Handle input change in page number field
  const onPageInputChange = (e) => {
    setInputPage(e.target.value);
  };

  // When user submits or presses enter in page number input
  const onPageInputSubmit = (e) => {
    e.preventDefault();
    const num = Number(inputPage);
    if (!isNaN(num) && num >= 1 && num <= totalPages) {
      setPage(num);
    } else {
      alert(`Please enter a valid page number between 1 and ${totalPages}`);
      setInputPage(String(page)); // reset input field to current page
    }
  };

  return (
    <div>
      <h2>Book List</h2>

      {/* Sort controls */}
      <div style={{ marginBottom: "1rem" }}>
        <label htmlFor="sortBy">Sort by: </label>
        <select id="sortBy" value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
          <option value="title">Title</option>
          <option value="author">Author</option>
          <option value="genre">Genre</option>
          <option value="publicationYear">Publication Year</option>
        </select>

        <select
          value={sortOrder}
          onChange={(e) => setSortOrder(e.target.value)}
          style={{ marginLeft: "1rem" }}
        >
          <option value="asc">Ascending</option>
          <option value="desc">Descending</option>
        </select>
      </div>

      {/* Loading and error */}
      {loading && <p>Loading books...</p>}
      {error && <p style={{ color: "red" }}>Error: {error}</p>}

      {/* Book list */}
      {!loading && !error && books.length === 0 && <p>No books found.</p>}
      {!loading && !error && books.length > 0 && (
        <ul>
          {books.map((book) => (
            <li key={book._id}>
              <b>{book.title}</b> - {book.author} ({book.publicationYear || "N/A"})
            </li>
          ))}
        </ul>
      )}

      {/* Pagination controls */}
      <div style={{ marginTop: "1rem" }}>
        <button onClick={() => setPage((p) => Math.max(1, p - 1))} disabled={page === 1 || loading}>
          Prev
        </button>

        {/* Page number input form */}
        <form
          onSubmit={onPageInputSubmit}
          style={{ display: "inline-block", margin: "0 1rem" }}
          aria-label="Page number form"
        >
          <label>
            Page{" "}
            <input
              type="number"
              min="1"
              max={totalPages}
              value={inputPage}
              onChange={onPageInputChange}
              style={{ width: "3rem" }}
              aria-label="Page number input"
            />
          </label>{" "}
          of {totalPages}
        </form>

        <button onClick={() => setPage((p) => Math.min(totalPages, p + 1))} disabled={page === totalPages || loading}>
          Next
        </button>
      </div>
    </div>
  );
}

export default BookList;
