import React, { useEffect, useState } from "react";

function BookList() {
  const [books, setBooks] = useState([]);
  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(5);
  const [sortBy, setSortBy] = useState("title");
  const [sortOrder, setSortOrder] = useState("asc");
  const [totalPages, setTotalPages] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchBooks = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch(
        `http://localhost:5000/api/books?page=${page}&page_size=${pageSize}&sort_by=${sortBy}&sort_order=${sortOrder}`
      );
      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }
      const data = await res.json();

      if (!data.books || !Array.isArray(data.books)) {
        throw new Error("Invalid response format: 'books' not found or not an array");
      }
      if (typeof data.totalPages !== "number") {
        throw new Error("Invalid response format: 'totalPages' not found or invalid");
      }

      setBooks(data.books);
      setTotalPages(data.totalPages);
    } catch (err) {
      setError(err.message);
      setBooks([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBooks();
  }, [page, pageSize, sortBy, sortOrder]);

  return (
    <div>
      <h2>ðŸ“š Book List</h2>

      {/* Sorting Controls */}
      <div style={{ marginBottom: "1rem" }}>
        <label htmlFor="sortBy">Sort by: </label>
        <select
          id="sortBy"
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
        >
          <option value="title">Title</option>
          <option value="author">Author</option>
          <option value="genre">Genre</option>
          <option value="publicationYear">Publication Year</option>
        </select>

        <select
          value={sortOrder}
          onChange={(e) => setSortOrder(e.target.value)}
          style={{ marginLeft: "1rem" }}
        >
          <option value="asc">Ascending</option>
          <option value="desc">Descending</option>
        </select>

        {/* Page Size Dropdown */}
        <label htmlFor="pageSize" style={{ marginLeft: "2rem" }}>Items per page: </label>
        <select
          id="pageSize"
          value={pageSize}
          onChange={(e) => {
            setPageSize(Number(e.target.value));
            setPage(1); // Reset to page 1 when page size changes
          }}
        >
          <option value={3}>3</option>
          <option value={5}>5</option>
          <option value={10}>10</option>
        </select>
      </div>

      {/* Loading/Error Display */}
      {loading && <p>Loading books...</p>}
      {error && <p style={{ color: "red" }}>Error fetching books: {error}</p>}
      {!loading && !error && books.length === 0 && <p>No books found.</p>}

      {/* Book List */}
      {!loading && !error && books.length > 0 && (
        <ul>
          {books.map((book) => (
            <li key={book._id}>
              <b>{book.title}</b> - {book.author} ({book.publicationYear || "N/A"})
            </li>
          ))}
        </ul>
      )}

      {/* Pagination Controls */}
      <div style={{ marginTop: "1rem" }}>
        <button
          onClick={() => setPage((p) => Math.max(1, p - 1))}
          disabled={page === 1 || loading}
        >
          Prev
        </button>

        <span style={{ margin: "0 1rem" }}>
          Page {page} of {totalPages}
        </span>

        <button
          onClick={() => setPage((p) => Math.min(totalPages, p + 1))}
          disabled={page === totalPages || loading}
        >
          Next
        </button>
      </div>
    </div>
  );
}

export default BookList;
